mapper.select:
SELECT * FROM
(SELECT  * FROM `basic` WHERE a36 > #{param2} and y45 > #{param3}) success
WHERE `name` LIKE #{param1}

SELECT * FROM
(SELECT  * FROM `basic` WHERE a36 >= #{param2} and y45 >= #{param3}) success
WHERE `lane` LIKE #{param1}

SELECT * FROM
(SELECT  * FROM `basic` WHERE a36 >= #{param2} and y45 >= #{param3}) success
WHERE `tablet` LIKE #{param1}

SELECT  * FROM `basic` WHERE `name` LIKE #{param1}
   AND lane LIKE #{param2} AND tablet LIKE #{param3} AND a36 >= #{param4}
     AND y45 >= #{param4}
